INSERT INTO `t_cast` VALUES ('199', '奥玛·希', 'Omar Sy', '男', '1220507.jpg', '1978-01-20', '法国', '奥马·希，法国男演员。因为与弗莱德·泰斯托特的喜剧组合“奥马和弗莱德”以及法国票房大作《不可触碰》而为人熟知。2012年凭借其在《不可触碰》里的表演获得了凯撒最佳男演员奖。', '1220507', 'https://img3.doubanio.com/view/celebrity/s_ratio_celebrity/public/p41401.jpg', '0');
